package ru.miit;import java.util.ArrayList;import java.util.Scanner;public class App {    private static int toInputNum() {        System.out.println("To enter num...");        Scanner scanner = new Scanner(System.in);        return scanner.nextInt();    }    private static int removeByIndex(NumStore srcArr) {        System.out.println("Source arr: " + srcArr.toString());        System.out.println("Type index of array element for remove (starts with [0])");        return new Scanner(System.in).nextInt();    }    private static boolean putAction() {        System.out.println("Put action: \n " +                "[+] - for add element \n" +                "[-] for remove element."        );        Scanner scanner = new Scanner(System.in);        String typeAction = scanner.toString();        return typeAction.equals("+");    }    public static void main(String[] args) {        NumStore store = new NumStore();        while (true) {            System.out.println("Put action: \n " +                    "[+] - for add element \n" +                    "[-] for remove element."            );            Scanner scanner = new Scanner(System.in);            String typeAction = scanner.toString();            if (typeAction.equals("+")) {                store.addToNumStore(toInputNum());            } else {                store.removeNumFromStoreByIndex(removeByIndex(store));            }            System.out.println(store.getNumStore());        }    }}