package ru.miit;import org.jboss.weld.environment.se.Weld;import org.jboss.weld.environment.se.WeldContainer;public class App {    public static void main(String[] args) {        Weld weld = new Weld();        WeldContainer container = weld.initialize();        NumStore arr = container.select(NumStore.class).get();        arr.addToNumStore(1);        arr.addToNumStore(2);        arr.addToNumStore(3);        arr.removeNumFromStoreByIndex(0);        container.shutdown();    }}